import * as api from '../api/index.js';


export const update_user = (user) => async(dispatch)=>{
    try {

      const {data} = await api.update_user(user);
      dispatch({type:'UPDATE', payload:data})

    } catch(err) {
      console.log(err.message);
    }
  }
