//import { GoogleLogin } from 'react-google-login';
import { Avatar, Button, Paper, Grid, Typography, Container,TextField } from '@material-ui/core';
import useStyles from './Styles';
import React,{ useState } from 'react';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import emailjs from 'emailjs-com';

const initialState = {fromEmail:'koosha@owji.com' ,toEmail: '', Subject: '', message: ''};

const Google = () => {

    const classes = useStyles();
    const [form, setForm] = useState(initialState);
    const dispatch = useDispatch();
    const history = useHistory();


    const Icon = () => (
    <svg style={{ width: '20px', height: '20px' }} viewBox="0 0 24 24">
        <path
        fill="currentColor"
        d="M21.35,11.1H12.18V13.83H18.69C18.36,17.64 15.19,19.27 12.19,19.27C8.36,19.27 5,16.25 5,12C5,7.9 8.2,4.73 12.2,4.73C15.29,4.73 17.1,6.7 17.1,6.7L19,4.72C19,4.72 16.56,2 12.1,2C6.42,2 2.03,6.8 2.03,12C2.03,17.05 6.16,22 12.25,22C17.6,22 21.5,18.33 21.5,12.91C21.5,11.76 21.35,11.1 21.35,11.1V11.1Z"
        />
    </svg>
    );
    const googleSuccess = async (res) => {
        const result = res?.profileObj;
        console.log(res)
        localStorage.setItem('profileG', JSON.stringify({...result ,accessToken: res?.accessToken}));
    };
    const googleError = () => alert('Google Sign In was unsuccessful. Try again later');
    const handleSubmit = (e) => {
        e.preventDefault();
        emailjs.send('service_1afl3rg', 'template_3ab06o2', initialState)
            .then(function(response) {
               console.log('SUCCESS!', response.status, response.text);
            }, function(error) {
               console.log('FAILED...', error);
            });
      };
      const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

return (
    <Container component="main" maxWidth="xs">             
          <Typography component="h1" variant="h5">
          Send Email
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit} noValidate>
            <Grid container spacing={2}>
                <Grid item xs={12} sm={12}>
                    <TextField
                    autoComplete="fname"
                    name="toEmail"
                    onChange={handleChange}
                    variant="outlined"
                    required
                    fullWidth
                    id="toEmail"
                    label="Email address"
                    autoFocus
                    />
                </Grid>
                <Grid item xs={12} sm={12}>
                    <TextField
                    autoComplete="fname"
                    name="Subject"
                    onChange={handleChange}
                    variant="outlined"
                    required
                    fullWidth
                    id="Subject"
                    label="Subject"
                    autoFocus
                    />
                </Grid>
                <Grid item xs={12} sm={12}>
                    <TextField
                    autoComplete="fname"
                    name="message"
                    onChange={handleChange}
                    variant="outlined"
                    required
                    fullWidth
                    multiline
                    id="message"
                    label="Text"
                    autoFocus
                    />
                </Grid>
                <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    className={classes.submit}
                >
                    Send Email
                </Button>
            </Grid>
        </form>
    </Container>
)
};
export default Google;
