import Fab from '@material-ui/core/Fab';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import UpdateIcon from '@material-ui/icons/Update';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';

import { useState, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { Typography } from '@material-ui/core';
//import { update_contact } from '../../actions/contact';


const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      margin: theme.spacing(1),
    },
    icon: {
      verticalAlign: 'bottom',
      height: 20,
      width: 20,
    },
    update: {

    }
}));


const  UpdateProfile=({currId})=> {
//const  UpdateProfile=({currId, allContacts})=> {
    const classes = useStyles();


    const dispatch = useDispatch();

    const [profileDetails, setProfileDetails]=useState({
        first_name: '',
        last_name: '',
        business: '',
        relationship: '',
        email_address:'', 
        phone_number:'', 
        description:''
    });

    //const contact = useSelector((state) => currId ? allContacts.find((n) => n._id === currId) : null);
    // add line to get user info
    const profile = {
        first_name: "Olivia", 
        last_name: "Ryan", 
        email_address: "email@gmail.com", 
        department: "No dept.", 
        role: "Teacher"
    }

    const [open, setOpen] = React.useState(false);


    useEffect(() => {
        if (profile){
            setProfileDetails({
                ...profileDetails,
                first_name: profile.first_name, 
                last_name: profile.last_name,
                email_address: profile.email_address,
                department: profile.department,
                role: profile.role

            })
        }
    }, [currId])

    const handleSaveClick = (event) => {
        console.log(profileDetails);
        if (profileDetails){

            //dispatch (update_contact(currId, contactDetails))
            //window.location.reload(true);
            handleClose();
        }

    }



    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    return (
    <div className={classes.root}>
            <div className = {classes.deleteContact}>
                <div className ='updateProfile'>
                    {/* <Button variant="contained" color="primary" size="large" width="100" onClick={handleClickOpen}>
                        Update details
                    </Button> */}
                <Fab variant="extended" color="primary" aria-label="update" onClick={handleClickOpen} style={{display:'flex'}} >
                    <UpdateIcon style={{marginRight:'.5rem'}}/>
                    Update
                </Fab>
                <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Update profile</DialogTitle>
                <Container component="main" maxWidth="xs">
        <TextField
                autoFocus
                margin="dense"
                id="name"
                label="First Name"
                type="first_name"
                fullWidth
                value = {profileDetails.first_name}
                onChange={(e) => setProfileDetails({...profileDetails, first_name:e.target.value})}
            ></TextField>
            <TextField
                autoFocus
                margin="dense"
                id="name"
                label="Last Name"
                type="last_name"
                fullWidth
                value = {profileDetails.last_name}
                onChange={(e) => setProfileDetails({...profileDetails, last_name:e.target.value})}
            ></TextField>
            <TextField
                autoFocus
                margin="dense"
                id="name"
                label="Department"
                type="department"
                fullWidth
                value = {profileDetails.department}
                onChange={(e) => setProfileDetails({...profileDetails, department:e.target.value})}
            ></TextField>
            <TextField
                autoFocus
                margin="dense"
                id="name"
                label="Role"
                type="role"
                fullWidth
                value = {profileDetails.role}
                onChange={(e) => setProfileDetails({...profileDetails, role:e.target.value})}
            ></TextField>
            <TextField
                autoFocus
                margin="dense"
                id="name"
                label="Email Address"
                type="email"
                fullWidth
                value = {profileDetails.email_address}
                onChange={(e) => setProfileDetails({...profileDetails, email_address:e.target.value})}
            ></TextField>
            <div className = 'note_footer'>
                <Button className = "Add to contacts" onClick={handleSaveClick}>Save changes</Button>
            </div>
    </Container>

                </Dialog>
                </div>

                </div>
            </div>

    );
};

export default UpdateProfile
