import Fab from '@material-ui/core/Fab';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import UpdateIcon from '@material-ui/icons/Update';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';

import { useState, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { FormLabel, Typography } from '@material-ui/core';
import { update_user } from '../../actions/user';


const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      margin: theme.spacing(1),
    },
    icon: {
      verticalAlign: 'bottom',
      height: 20,
      width: 20,
    },
    update: {

    }
}));

//const  UpdateProfile=({profile})=> {
const  UpdateProfile=()=> {
    const classes = useStyles();
    const dispatch = useDispatch();
    const firstState = {first_name: "hello"};
    const profile = {...JSON.parse(localStorage.getItem('profile')),...firstState};
    const profile1 = {...JSON.parse(localStorage.getItem('profile')).result,...firstState};
    const [profileDetails, setProfileDetails]=useState({
        first_name: profile.result.first_name, 
        last_name: profile.result.last_name,
        email_address: profile.result.email_address,
        department: profile.result.department,
        role: profile.result.role,
        email_service: profile.result.email_service, 
    });

    //const contact = useSelector((state) => currId ? allContacts.find((n) => n._id === currId) : null);



    const [open, setOpen] = React.useState(false);

    // useEffect(() => {
    //      if (profile){
    //         setProfileDetails({
    //             first_name: profile[0].first_name, 
    //             last_name: profile[0].last_name,
    //             email_address: profile[0].email_address,
    //             department: profile[0].department,
    //             role: profile[0].role

    //         })
    //     }
    // }, //[currId]
    // )
    console.log(profile1);
    const handleSaveClick = (event) => {
        event.preventDefault();
        // console.log(profileDetails);
        handleClose();
        // if (profileDetails){

        //     dispatch (update_user(profileDetails))
        //     window.location.reload(true);
        //     handleClose();
        // }
    }
    const handleChange = (e) => setProfileDetails({ ...profileDetails, [e.target.id]: e.target.value });

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    return (
    <div className={classes.root}>
            <div className = {classes.deleteContact}>
                <div className ='updateProfile'>
                {/* Update button */}
                <Fab color="primary" aria-label="update" onClick={handleClickOpen} style={{display:'flex'}} >
                    <UpdateIcon/>
                </Fab>
                {/* Title of update pop-up */}
                <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Update Profile</DialogTitle>
                <Container component="main" maxWidth="xs">
                    <TextField
                        autoFocus
                        margin="dense"
                        id="first_name"
                        label="First Name"
                        type="first_name"
                        fullWidth
                        value = {profileDetails.first_name}
                        onChange={handleChange}
                    ></TextField>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="last_name"
                        label="Last Name"
                        type="last_name"
                        fullWidth
                        value = {profileDetails.last_name}
                        onChange={handleChange}
                    ></TextField>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="email_address"
                        label="Email Address"
                        type="email"
                        fullWidth
                        value = {profileDetails.email_address}
                        onChange={handleChange}
                    ></TextField>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="department"
                        label="Department"
                        type="department"
                        fullWidth
                        value = {profileDetails.department}
                        onChange={handleChange}
                    ></TextField>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="role"
                        label="Role"
                        type="role"
                        fullWidth
                        value = {profileDetails.role}
                        onChange={handleChange}
                    ></TextField>

                    <div className = 'note_footer'>
                        {/* <Button className = "Save user update" onClick={handleSaveClick}>Save changes</Button> */}
                        <Button className = "Save user update" onClick={handleSaveClick}>Save changes</Button>
                    </div>
                </Container>

                </Dialog>
                </div>

                </div>
            </div>

    );
};

export default UpdateProfile
